        (933, '2019-06-11 14:16:52.968000', 'The Department of Molecular Genetics investigates the molecular, cellular and genetic mechanisms that underlie basic biological processes in complex organisms, such as human, mouse and fruit fly, at the level of single cell organisms, including bacteria and yeast.', 'https://www.weizmann.ac.il/molgen/', '2023-04-11 12:45:02.059000', 'Department of Molecular Genetics, Weizmann Institute of Science, Rehovot', 'IL', null),
        (525, '2019-06-11 14:16:18.141000', 'The Forsyth Institute''s mission is to improve oral and overall health through a combination of innovative research and community outreach by accelerating the delivery of wellness solutions locally and globally.', 'https://www.forsyth.org/', '2023-06-12 12:55:36.051000', 'The Forsyth Institute, Boston', 'US', null),
        (415, '2019-06-11 14:16:08.135000', 'The University of Amsterdam is ambitious, creative and committed: a leader in international science and a partner in innovation, the UvA has been inspiring generations since 1632. Read our mission and vision.', 'https://www.uva.nl/en/home', '2023-06-12 14:14:07.447000', 'University of Amsterdam', 'NL', 'https://ror.org/04dkp9463'),
        (295, '2019-06-11 14:15:56.878000', 'Dedicated to the well-being of all people and guided by science, the World Health Organization leads and champions global efforts to give everyone, everywhere an equal chance to live a healthy life. ', 'https://www.who.int/', '2023-06-12 15:18:54.634000', 'World Health Organisation', 'CH', null),
